import Navbar from './components/Nav';
import React, { useEffect, useState, createContext } from 'react';
import './styles/calendar.scss';
import './styles/App.css';
import Login from './components/Login';
import Home from './components/Home';
//import Home from './components/newhome';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Createevent from './components/CreateEvent';
import './styles/Event.scss';
import axios from 'axios';
import Friends from './components/Friends';
import BetaFooter from './components/BetaFooter';
import Edit, {LinkState} from './components/Edit';
import './styles/toruskit.css';
import Chat from './components/Chat/chatHome';
import { useCycle } from 'framer-motion';
import { Loader} from "halogen"
//use via props vs via a freq called func becasue it will give better performance!
export const userData = createContext();
const App = () => {
   //create a function here that will create the props data. if it cannot find the data for the prop, redirect and/or return null.
   const [state, setState] = useState({
      username: <Loader color="#26A65B" size="16px" margin="4px" />,
      data: null,
      events: null,
      friendEvents: null,
      /*--------------------*/
      friendReqs: null,
      friends: null,
      /*--------------------*/
      month: null,
      year: null,
      /* ------------------ */
      chats: null,
      messages: null,
   });
   //get to removeing the constructors!!!

   useEffect(() => {
      getData();
      setInterval(() => {
         console.log('refreshing...');
         getData();
      }, 15000);
      //localize???
   }, []);

   //put events into state
   //console.log(state, 'state from appjs');

   return (
      <userData.Provider value={state}>
         <Router>
            <Navbar/>
            <Switch>
               <Route path="/" exact component={Login} />
               <Route path="/home" exact component={Home} />
               <Route
                  exact
                  path="/home/:day/:month/:year"
                  component={Createevent}
               />
               <Route exact path="/friends" component={Friends} />
               <Route exact path="/chat" component={Chat} />
               <Route exact path='/edit/:id' component={Edit} />
               <Route render={() => <h1>404 error</h1>} />
            </Switch>
            {/* <BetaFooter /> */}
         </Router>
      </userData.Provider>
   );
   function getData() {
      const cookie = localStorage.getItem('token');
      if (cookie) {
         if (window.location.pathname === '/') {
            window.location.pathname = '/home';
         }
         axios
            .get('http://localhost:8000/getuser', {
               headers: { Authorization: `Bearer ${cookie}` }
            })
            .then((res) => {
               console.log(res.data, 'res');
               //console.log(res.data.messages[0])
               setTimeout(()=>
               setState({
                  username: res.data.user.username,
                  data: res.data.user,
                  events: res.data.events,
                  id:res.data.user.id,
                  /*--------------------*/
                  incoming: res.data.incoming,
                  outgoing: res.data.outgoing,
                  friends: res.data.friends,
                  /*--------------------*/
                  refresh: () => getData(),
                  /*--------------------*/
                  chats: res.data.chats,//.sort(getNewestChat),
                  messages: res.data.messages
               }),2000)
            })
            .catch((err) => {
               //if the cookie is bad, then login
               console.log("in catch block, fatal req error",err)
               localStorage.removeItem('token');
               window.location.pathname = '/';
            });
         //this should ONLY be triggered when the cookie is null and the pathname is not login, so a user is not found.
      } else if (window.location.pathname != '/') {
         //if there is no cookie, the redirect will go through, but the component will handle the rejection.
         //console.log('error!');
         // <Redirect to="/"/>
         window.location.pathname = '/';
      }
   }
};
export default App;
