import React, { useContext, useEffect, useRef, useState } from 'react';
import { socket, Socket } from '../App';
import axios from 'axios';
import { useCycle, useExternalRef } from 'framer-motion';
import Progress from '../microComponents/progress';
import { motion } from 'framer-motion';
export default function FriendMenu() {
   const sockeet = useContext(Socket);
   useEffect(() => {
      socket.on('requested', (m) => {
         console.log('requested!');
      });
   });
   const [username, setUsername] = useState('');
   const err = useRef();
   return (
      <div className="m-4">
         <input
            onChange={(e) => setUsername(e.target.value)}
            value={username}
            className="form-control w-25"
         />
         <br />
            <textarea
               className="form-control"
               style={{ resize: 'none', width: '17%' }}
               value={message}
               onChange={(e) => setMessage(e.target.value)}
            />
            <Progress done={message.length} complete={30} size={'17%'} />
         <br />
         <b>
            <span ref={err} />
         </b>
         <br />
         <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.9 }}
            className="btn btn-sm btn-primary"
            onClick={() => requestUser(username, message)}
         >
            send
         </motion.button>
      </div>
   );
   async function requestUser(requested, message) {
      const cookie = localStorage.getItem('token');
      try {
         const user = await axios.post(
            'http://localhost:8000/adduser',
            { requested: requested, message: message },
            { headers: { Authorization: 'Bearer ' + cookie } }
         );
         console.log('REQUESTED USER!', user);
         setMessage('');
         setUsername('');
         Respond(
            err,
            `Sucessfully sent a friend request to: ${requested}`,
            'text-success',
            3000
         );
      } catch (e) {
         console.error(e, 'FAILED TO REQUEST');
         Respond(
            err,
            `Failed to send a friend request to: ${requested}`,
            'text-danger',
            3000
         );
      }
   }
   function Respond(ref, message, className, time) {
      ref.current.classList.toggle(className);
      ref.current.innerHTML = message;
      setTimeout(() => {
         ref.current.innerHTML = '';
         ref.current.classList.toggle(className);
      }, time);
   }
}
