import React, { useEffect, useState, useContext, useRef } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { userData } from '../App';
import { Link } from 'react-router-dom';
import Incoming from './Friends/incoming';
import Outgoing from './Friends/outgoing';
import Friend from './Friends/friend';
import { capitalize } from 'lodash';
const Friends = (props) => {
   const user = useContext(userData);
   const variants = {
      hidden: { opacity: 0 },
      visible: { opacity: 1 },
   };

   const [username, setUsername] = useState('')

   async function request1() {
      const cookie = localStorage.getItem('token');
      //console.log(username.current.value);
      axios
         .post(
            'http://localhost:8000/reqfriend',
            { user: username },
            {
               headers: {
                  Authorization: 'Bearer ' + cookie,
               },
            }
         )
         .then((res) => {
            user.refresh()
            console.log('?', res);
            setError({
               message: 'Sent request to ' + username,
               class: 'text-success',
            });

         })
         .catch((err) => {
            console.log(err, 'caught');
            let { status } = err.response;

            switch (status) {
               case 404:
                  setError({
                     message: 'This user does not exist!',
                     class: 'error',
                  });
                  break;
               case 400:
                  setError({
                     message: "You've already requested this user!",
                     class: 'error',
                  });
                  break;
               case 403:
                  setError({
                     message: 'You cannot request yourself!',
                     class: 'error',
                  });
                  break;
               case 409:
                  setError({
                     message: 'You are already friends with this user!',
                     class: 'error',
                  });
            }
         });
       user.refresh();
   }
   async function handleAccept(id) {
      const cookie = localStorage.getItem('token');
      return await axios.post(
         'http://localhost:8000/acceptfriend/' + id,
         {},
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
   async function handleDecline(id) {
      console.log(id);
      console.log('deleting...');
      const cookie = localStorage.getItem('token');
      return await axios.post(
         'http://localhost:8000/deleterequest/' + id,
         {},
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
   async function handleRemove(id, friend) {
      const cookie = localStorage.getItem('token');
      return await axios.post(
         'http://localhost:8000/removefriend/' + id + '/' + friend,
         {},
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
   async function takeBack(id) {
      const cookie = localStorage.getItem('token');
      return await axios.post(
         'http://localhost:8000/takeback/' + id,
         {},
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
   const [error, setError] = useState({ message: null, class: null });

   return (
      <div className="mx-5 my-4">
         <h1>{capitalize(user.username)}'s friend page!</h1>
         <input
            onChange={(e) => setUsername(e.target.value)}
            value={username}
            className="form-control w-50"
            style={{ borderRadius: 13 }}
         />
         <div className="float-right">
            {( user.incoming.length + user.outgoing.length) >
            0 && (
               <motion.h2
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.3 }}
               >
                  Requests
               </motion.h2>
               )}
            {user.incoming && 
               user.incoming.map((i, index) => (
                  <Incoming
                     index={index}
                     onDecline={() => handleDecline(i.relationship)}
                     onAccept={() =>
                        handleAccept(i.relationship).then(() => user.refresh())
                     }
                     name={i.username}
                  />
               ))}
            {user.outgoing &&
               user.outgoing.map((i, index) => (
                  <Outgoing
                     index={index}
                     name={i.username}
                     onTakeBack={() =>
                        takeBack(i.relationship).then(() => user.refresh())
                     }
                  />
               ))}




            {user.friends.length > 0 && (
               <motion.h2
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.3 }}
               >
                  Friends
               </motion.h2>
            )}
            {user.friends.map((i, index) => (
               <Friend
                  index={index}
                  onRemove={() =>
                     handleRemove(user.data.id, i.id).then(() => user.refresh())
                  }
                  name={i.username}
               />
            ))}
         </div>
         <span className={error.class}>{error.message}</span>
         <br />

         <motion.button
            className="btn-primary btn"
            onClick={() => {
               request1();
            }}
            whileTap={{ scale: 0.9 }}
            whileHover={{ scale: 1.1 }}
         >
            Request
         </motion.button>

         <br />
         <Link to="/home">
            <motion.button
               className="btn-secondary btn btn-sm my-4"
               whileTap={{ scale: 0.9 }}
               whileHover={{ scale: 1.1 }}
            >
               {' '}
               &#10094; home
            </motion.button>
         </Link>
      </div>
   );
};
export default Friends;
