import React, { useState, useContext } from 'react';
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import userData from '../App';
import '../styles/nav.scss';
export default function Navbar() {
   const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
   ];
   const [margin, setMargin] = useState({ margin: '10px' });
   const [month, setMonth] = useState(null);

  useEffect(() => {
     window.addEventListener('scroll', (e) => {
        if (window.scrollY >= 40) {
           setMargin({ margin: '10px 60px' });
        } else {
           setMargin({ margin: '10px' });
        }
     });
     console.log(user,'adfasdfasdf')
  },[])
   const user = useContext(userData);
   const [events, setEvents] = useState(user && user.events);
   useEffect(() => {
      setEvents(user&&user.events) 
   },[user])
   return (
      <nav
         class="navbar navbar-expand-lg navbar-light bg-transparent pl-2"
         style={margin}
      >
         <span class="navbar-brand" href="#">
            &nbsp;Yocal
         </span>
         <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNavDropdown"
            aria-controls="navbarNavDropdown"
            aria-expanded="false"
            aria-label="Toggle navigation"
         >
            <span class="navbar-toggler-icon"></span>
         </button>
         <div class="collapse navbar-collapse" id="navbarNavDropdown">
            <ul class="navbar-nav">
               <li class="nav-item active">
                  <Link class="nav-link" to="/home">
                     Home <span class="sr-only">(current)</span>
                  </Link>
               </li>
               <li class="nav-item">
                  <Link class="nav-link" to="/friends">
                     Friends
                  </Link>
               </li>
               <li className="nav-item">

                     <h4 className="bg-info btn">
                        {/* {
                           user.events.filter(
                              (i) => new Date(i.date) > new Date()
                           ).length
                        } */}hi
                     </h4>
                     <h4 className="bg-danger btn">
                        {/* {
                           user.events.filter(
                              (i) => new Date(i.date) < new Date()
                           ).length
                        } */}hi
                     </h4>
                     <h4 className="bg-success btn">
                        {
                           user.events.filter(
                              (i) => new Date(i.date) == new Date()
                           ).length.toString()
                        } 
                     </h4>
               </li>
               {/*<li class="nav-item">
            <Link class="nav-link" to="/chat">Chat</Link>
        </li>*/}
            </ul>
         </div>
      </nav>
   );
}
