import React, { useContext, useRef, useState } from 'react';
import { userData } from '../App';
import { motion, useMotionTemplate } from 'framer-motion';
import { Link, useParams, useLocation } from 'react-router-dom';
import { useEffect } from 'react';
import axios from 'axios';
import Event from '../components/animatedEvent';
import Progress from '../microComponents/progress';
import '../styles/forms.scss';
import { useHistory } from 'react-router-dom';
import { createCalendar, highlightDate } from './functions/calendars'
import "../styles/edit.scss";
   import "../styles/calendar.scss"

Date.prototype.getInfo = function () {
   return {
      day: this.getDate(),
      month: this.getMonth(),
      year: this.getFullYear(),
      hour:
         this.getHours() > 12
            ? this.getHours() % 12
            : this.getHours() == 0
            ? 12
            : this.getHours(),
      minute: this.getMinutes(),
      tod: this.getHours() > 12 ? 2 : 1,
      obj: this,
   };
};
//add date changing!!
export default function Edit(props) {
   const history = useHistory();
   const user = useContext(userData);

   const { id } = useParams();
   //useparams
   const [event, setEvent] = useState(user.events && user.events[id]);
   useEffect(() => {
      setEvent(user.events && user.events[id]);
      console.log(event, 'fsdfasdf');
   }, []);
   const [error, setError] = useState('');
   function updateTitle(e) {
      if (e.target.value.length > 60)
         return setError(
            'your event title must be between 4 and 60 characters'
         );
      setEvent({
         ...event,

         title: e.target.value,
         date: event.date,
         duration: event.duration,
      });
   }
   useEffect(() => {
      console.log(event);
   }, [event]);

   return (
      <div className="p-5 edit-flex">
         <div>
            <div className="selectors-flex">
               <select
                  id="hour"
                  className="form-select form-select-sm"
                  style={{ width: '100px', borderRadius: 10 }}
                  name="hour"
                  value={new Date(event.date).getHours()}
                  onChange={(e) =>
                     setEvent({
                        ...event,
                        title: event.title,
                        date: new Date(event.date).setHours(e.target.value),
                        duration: event.duration,
                     })
                  }
               >
                  {[...Array(12).keys()].map((item) => (
                     <option value={item} key={item++}>
                        {item}
                     </option>
                  ))}
               </select>
               &nbsp;
               <select
                  className="form-select form-select-sm"
                  style={{ width: '100px', borderRadius: 10 }}
                  onChange={(e) =>
                     setEvent({
                        ...event,
                        title: event.title,
                        date: new Date(event.date).setMinutes(e.target.value),
                        duration: event.duration,
                     })
                  }
                  value={new Date(event.date).getMinutes().toString()}
               >
                  {[...Array(2).keys()].map((item) => (
                     <option key={item}>{'0' + 5 * item}</option>
                  ))}
                  {[...Array(10).keys()].map((item) => (
                     <option key={item++}>{5 * (item + 1)}</option>
                  ))}
               </select>
               &nbsp;
               <select
                  style={{ width: '100px', borderRadius: 10 }}
                  className="form-select form-select-sm"
                  id="tod"
                  name="tod"
                  value={new Date(event.date).getInfo().tod == 1 ? 'AM' : 'PM'}
                  onChange={(e) =>
                     setEvent({
                        ...event,
                        title: event.title,
                        date: new Date(event.date).setHours(
                           (new Date(event.date).getHours() + 12) % 24
                        ),
                        duration: e.target.value,
                     })
                  }
               >
                  <option>AM</option>
                  <option>PM</option>
               </select>
               &nbsp;
               <div style={{ marginTop: '4px' }}>&nbsp;for&nbsp;</div>
               &nbsp;
               <select
                  style={{ width: '130px', borderRadius: 10 }}
                  className="form-select form-select-sm"
                  onChange={(e) =>
                     setEvent({
                        ...event,
                        title: event.title,
                        date: event.date,
                        duration: e.target.value,
                     })
                  }
               >
                  {[...Array(4).keys()].map((i) =>
                     15 * i < 10 ? (
                        <option value={0}>No duration</option>
                     ) : (
                        //set VCALUES TO THE MINUTE UNIT!!!
                        <option value={15 * i}>{15 * i} Minutes</option>
                     )
                  )}
                  <option
                     selected
                     value={60}
                     selected={event.duration == 60 ? true : false}
                  >
                     1 Hour
                  </option>
                  {[...Array(5).keys()].map((i) => (
                     <option
                        value={60 * (2 + i)}
                        selected={event.duration == 60 * (2 + i) ? true : false}
                     >
                        {2 + i} Hours
                     </option>
                     //UPLOAD TO DB AS A DATE
                  ))}
                  <option
                     value={9 * 60}
                     selected={event.duration == 9 * 60 ? true : false}
                  >
                     9 Hours
                  </option>
               </select>
            </div>
            <br />
            <div className="float-left">
               <input
                  className="form-control create-event"
                  value={event && event.title}
                  onChange={(e) => updateTitle(e)}
               />
               <Progress done={event.title.length} />
               <br />
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  whileTap={{
                     scale: 0.9,
                  }}
                  className="btn btn-primary"
                  onClick={() =>
                     update(event)
                        .then(() => {
                           console.log('sucess!');
                           user.refresh();
                           history.push('/home');
                        })
                        .catch(() => {})
                  }
               >
                  Confirm edits
               </motion.button>
               <br />
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  whileTap={{
                     scale: 0.9,
                  }}
                  className="btn btn-sm btn-danger mt-2"
                  onClick={() => setEvent(user.events && user.events[id])}
               >
                  Revert changes
               </motion.button>
               <br />
               <br />
               {/* <Link to="/home"> */}
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  whileTap={{
                     scale: 0.9,
                  }}
                  className="btn btn-sm btn-secondary"
                  onClick={() => history.goBack()}
               >
                  &#10094; home
               </motion.button>
               {/* </Link> */}
            </div>
         </div>
         <div className="float-right">
            <ul className="edit-cal days">
               {createCalendar(5, 2020).map((item) => (
                  <li>
                     {item == -1 ? (
                        <span className="wrong">
                           {item == -1 ? '-' : item + 1}
                        </span>
                     ) : (
                        <Link to={`/home/${1 + item}/${5}/${2020}`}>
                           <span
                              className={highlightDate(item, {
                                 month: 5,
                                 year: 2020,
                              })}
                           >
                              {item + 1}
                           </span>
                        </Link>
                     )}
                  </li>
               ))}
            </ul>
         </div>
         {/* <h2>{event && event.date}</h2>
         {new Date(event.date).toString()} */}
      </div>
   );

   async function update(Newevent) {
      const cookie = localStorage.getItem('token');
      return await axios.post(
         `http://localhost:8000/editevent`,
         {
            creator: Newevent.creator,
            duration: Newevent.duration,
            title: Newevent.title,
            date: Newevent.date,
            id: Newevent.eventid,
         },
         {
            headers: { Authorization: `Bearer ${cookie}` },
         }
      );
   }
}
