import React from 'react';
import { motion, useCycle } from 'framer-motion';
import { variants } from '../../styles/animations';
import { useContext } from 'react';
import { userData } from '../../App';
import Axios from 'axios';
export default function ChatSelect(props) {
   const user = useContext(userData);
   console.log(props, 'props');
   const chatVariants = {
      hidden: (i) => ({
         opacity: 0,
         y: i * 25 - 100,
      }),
      visible: (i) => ({
         opacity: 1,
         y: 0,
         transition: {
            delay: i * 0.1 + 0.2,
            type: 'spring',
         },
      }),
      hide: { opacity: 0 },
   };
   const [popup, cyclePopup] = useCycle(false, true);
   return (
      <motion.div
         layout
         onClick={() => props.onSelect()}
         variants={chatVariants}
         initial="hidden"
         animate="visible"
         custom={props.index}
         className={"card bg-"+(props.selected==true?"success":"info")}
         style={{
            width: '20vw',
            height: '100px',
            marginTop: '20px',
         }}
      >
         <h3 className="m-3">{props.title}</h3>
         <motion.span>
            {popup == false ? (
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  className="btn xxs btn-secondary py-0 px-1"
                  onClick={() => {
                     cyclePopup();
                     setTimeout(() => {
                        cyclePopup();
                     }, 2000);
                  }}
                  whileTap={{ scale: 0.9 }}
               >
                  <span>Leave chat</span>{' '}
               </motion.button>
            ) : (
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  className="btn xxs btn-danger py-0 px-1"
                  onClick={() =>
                     props.onLeave()
                  }
                  whileTap={{ scale: 0.9 }}
               >
                  Are you sure?
               </motion.button>
            )}
         </motion.span>
      </motion.div>
   );
   async function leaveChat(relationship) {
      console.log(relationship, 'relationshipo');
      const cookie = localStorage.getItem('token');
      return await Axios.post(
         'http://localhost:8000/leavechat/' + relationship,
         {},
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
}
