import React, { useRef } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { motion, useAnimation, useCycle } from 'framer-motion';
import { useContext, useState } from 'react';
import { userData } from '../App';
import { useEffect } from 'react';
import { AnimateSharedLayout } from 'framer-motion';
import AddFreind from './Friends/friendEventCard';
import { forwardRef } from 'react';
import AddFriend from './Friends/friendEventCard';
import Progress from '../microComponents/progress';
import '../styles/create.scss';
const variants = {
   hidden: { opacity: 0 },
   visible: { opacity: 1 },
};
const months = [
   'January',
   'February',
   'March',
   'April',
   'May',
   'June',
   'July',
   'August',
   'September',
   'October',
   'November',
   'December',
];
const CreateEvent = (props) => {
   const user = useContext(userData);

   const formatYear = () => {
      const date = new Date();
      if (date.getFullYear() == props.match.params.year) {
         return '';
      } else {
         return props.match.params.year;
      }
   };
   const submitEvent = (
      hour,
      minute,
      tod,
      day,
      month,
      year,
      title,
      duration
   ) => {
      console.log(day);
      const eventDate = new Date(
         `${months[month]} ${day} ${year} ${hour}:${minute} ${tod}`
      );
      console.log(eventDate, 'evetnt date');
      axios
         .post(
            'http://localhost:8000/createevent',
            {
               date: eventDate,
               title: title,
               friends: added,
               duration: duration,
            },
            {
               headers: {
                  Authorization: 'Bearer ' + localStorage.getItem('token'),
               },
            }
         )
         .then(() => {
            window.location.href = '/home';
         })
         .catch((e) => console.log('ERROR! '));
   };

   const //title = useRef(),
      hour = useRef(),
      mins = useRef(),
      tod = useRef(),
      errors = useRef(),
      duration = useRef();
   const controls = useAnimation();

   const [friendsShown, showFriends] = useCycle(false, true);
   const [added, setAdded] = useState([]);

   return (
      <>
         <div className="px-5 pt-5">
            <motion.div
               initial={{ x: 100 }}
               animate={{ x: 0, delay: 10000 }}
               className="friends mx-5 float-right"
            >
               {friendsShown &&
                  user.friends.map((i) => (
                     <AddFriend
                        name={i.username}
                        onAdd={() => AddFriendToEvent(i.id)}
                        onRemove={() => RemoveFriendFromEvent(i.id)}
                     />
                  ))}
               {console.log(user.friends, 'user.fiends')}
            </motion.div>
            <div className="px-4">
               <h1 id="curdate">
                  Scheduling event for {months[props.match.params.month]}{' '}
                  {props.match.params.day} {formatYear()}
               </h1>
               <div className="selectors-flex">
                  <select
                     id="hour"
                     className="form-select form-select-sm"
                     style={{ width: '100px', borderRadius: 10 }}
                     ref={hour}
                     name="hour"
                  >
                     <option selected disabled>
                        Hour
                     </option>
                     {[...Array(12).keys()].map((item) => (
                        <option value={item} key={item++}>
                           {item}
                        </option>
                     ))}
                  </select>
                  &nbsp;
                  <select
                     ref={mins}
                     className="form-select form-select-sm"
                     style={{ width: '100px', borderRadius: 10 }}
                  >
                     <option selected disabled>
                        Minute
                     </option>
                     {[...Array(2).keys()].map((item) => (
                        <option key={item}>{'0' + 5 * item}</option>
                     ))}
                     {[...Array(10).keys()].map((item) => (
                        <option key={item++}>{5 * (item + 1)}</option>
                     ))}
                  </select>
                  &nbsp;
                  <select
                     style={{ width: '100px', borderRadius: 10 }}
                     className="form-select form-select-sm"
                     ref={tod}
                     id="tod"
                     name="tod"
                  >
                     <option disabled selected>
                        AM/PM
                     </option>
                     <option>AM</option>
                     <option>PM</option>
                  </select>
                  &nbsp;
                  <div style={{ marginTop: '4px' }}>&nbsp;for&nbsp;</div>
                  &nbsp;
                  <select
                     style={{ width: '100px', borderRadius: 10 }}
                     className="form-select form-select-sm"
                     ref={duration}
                     onChange={() => console.log(duration.current.value)}
                  >
                     {[...Array(4).keys()].map((i) =>
                        15 * i < 10 ? (
                           <option value={0}>No duration</option>
                        ) : (
                           //set VCALUES TO THE MINUTE UNIT!!!
                           <option value={15 * i}>{15 * i} Minutes</option>
                        )
                     )}
                     <option selected value={60}>
                        1 Hour
                     </option>
                     {[...Array(5).keys()].map((i) => (
                        <option value={60 * (2 + i)}>{2 + i} Hours</option>
                        //UPLOAD TO DB AS A DATE
                     ))}
                     <option value={9 * 60}>9 Hours</option>
                  </select>
                  <input
                     onClick={() => {
                        showFriends();
                        setAdded([]);
                     }}
                     type="checkbox"
                     className="form-check-input mt-2 mx-3"
                     //style={{}}
                  />
               </div>
               <br />
               &nbsp;&nbsp;
               <div className="float-left">
                  <input
                     style={{ borderRadius: 13 }}
                     onChange={(e) => updateTitle(e)}
                     value={title}
                     className="form-control create-event"
                  />
                  <Progress done={title.length} />
               </div>
               <span className="error" ref={errors}></span>
               <br />
               <br />
               <motion.button
                  whileHover={{
                     scale: 1.2,
                  }}
                  whileTap={{
                     scale: 0.9,
                  }}
                  onClick={validateOptions}
                  className="btn btn-primary"
               >
                  Create event
               </motion.button>
               <br />
               <br />
               <Link to="/home">
                  <motion.button
                     whileHover={{
                        scale: 1.2,
                     }}
                     whileTap={{
                        scale: 0.9,
                     }}
                     className="btn btn-sm btn-secondary"
                  >
                     &#10094; home
                  </motion.button>
               </Link>
            </div>
         </div>
         {/* <br/><br/><br/> */}
      </>
   );
   const validateOptions = () => {
      if (title.length < 4 || title.length > 60) {
         return (errors.current.innerHTML =
            'your event name must be between 4 and 60 characters');
      } else if (
         hour.current.value.toLowerCase() == 'hour' ||
         mins.current.value.toLowerCase() == 'minutes' ||
         tod.current.value.toLowerCase() == 'am/pm'
      ) {
         return (errors.current.innerHTML =
            'please enter a valid time for your event');
      }
      submitEvent(
         parseInt(hour.current.value) + 1,
         mins.current.value,
         tod.current.value,
         props.match.params.day,
         props.match.params.month,
         props.match.params.year,
         title,
         parseInt(duration.current.value)
      );
      //make duration date
      window.location.href = '/home';
   };
   async function AddFriendToEvent(id) {
      console.log([...added], 'added but spread');
      let idk = [...added];
      idk.push(id);
      setAdded(idk);
      console.log(added, 'after');
   }
   async function RemoveFriendFromEvent(id) {
      console.log([...added], 'added but spread');
      let idk = [...added];
      idk.splice(idk.indexOf(id), 1);
      setAdded(idk);
      console.log(added, 'after');
   }
   const [title, setTitle] = useState('');
   function updateTitle(e) {
      console.log(e.target.value);
      if (e.target.value.length > 60) return 0;
      setTitle(e.target.value);
   }
};

export default CreateEvent;
