   );
   function getData() {
      const cookie = localStorage.getItem('token');
      if (cookie) {
         if (window.location.pathname === '/') {
            window.location.pathname = '/home';
         }
         axios
            .get('http://localhost:8000/getuser', {
               headers: { Authorization: `Bearer ${cookie}` },
            })
            .then((res) => {
               console.log(res.data, 'res');
               //  setTimeout(()=>
               const meep = new Map(res.data.events)
               console.log(meep.valify());
               setState({
                  username: res.data.user.username,
                  data: res.data.user,
                  events: meep,//.valify(),
                  //FIX THIS!!!!!
                  id: res.data.user.id,
                  /*--------------------*/
                  incoming: res.data.incoming,
                  outgoing: res.data.outgoing,
                  friends: res.data.friends,
                  /*--------------------*/
                  refresh: () => getData(),
                  /*--------------------*/
                  conflicts: res.data.conflicts,
               }); //,
               // 3000
               console.log(meep.get(362),"meepget0")
               //   );

            })
            .catch((err) => {
               //if the cookie is bad, then login
               console.log('in catch block, fatal req error', err);
               localStorage.removeItem('token');
               window.location.pathname = '/';
            });
         //this should ONLY be triggered when the cookie is null and the pathname is not login, so a user is not found.
      } else if (window.location.pathname != '/') {
         //if there is no cookie, the redirect will go through, but the component will handle the rejection.
         //console.log('error!');
         // <Redirect to="/"/>
         window.location.pathname = '/';
      }
   }


