import React from 'react';
import ChatSelect from './chatSelect';
import { motion, useCycle } from 'framer-motion';
import { useContext, useState } from 'react';
import { useRef } from 'react';
import Axios from 'axios';
import { userData } from '../../App';
export default function NewChat(props) {
   const user = useContext(userData);
   const chatVariants = {
      hidden: (i) => ({
         opacity: 0,
         y: i * 25 - 100,
      }),
      visible: (i) => ({
         opacity: 1,
         y: 0,
         transition: {
            delay: i * 0.1 + 0.2,
            type: 'spring',
         },
      }),
      hide: { opacity: 0 },
   };
   const [confirmed, Confirm] = useCycle(false, true);
   const [name, setName] = useState('');
   const curname = useRef();
   const [alive, Kill] = useCycle(true, false);
   async function createChat(name) {
      console.log('creating chat with name');
      const cookie = localStorage.getItem('token');
      return await Axios.post(
         'http://localhost:8000/createchat',
         { name: name },
         { headers: { Authorization: 'Bearer ' + cookie } }
      );
   }
   return (
      <motion.span layout>
         {alive == true ? (
            <motion.span>
               {confirmed == false ? (
                  <motion.div
                     layout
                     variants={chatVariants}
                     initial="hidden"
                     animate="visible"
                     custom={props.index}
                     className="card bg-info"
                     style={{
                        width: '400px',
                        height: '100px',
                        marginTop: '20px',
                     }}
                  >
                     <motion.input
                        value={name}
                        ref={curname}
                        onChange={() => setName(curname.current.value)}
                        className="m-2 new-chat-input form-control"
                        style={{ width: '180px' }}
                     />
                     <motion.button
                        className="xxs btn btn-secondary px-1 py-0"
                        style={{ position: 'absolute', bottom: 2 }}
                        onClick={() => createChat(name).then(() => {
                           //Confirm()
                           user.refresh()
                           Kill()
                        })}
                     >
                        confirm
                     </motion.button>
                     <motion.button
                        className="xxs btn btn-secondary px-1 py-0"
                        style={{ position: 'absolute', left: 57, bottom: 2 }}
                        onClick={() => Kill()}
                     >
                        delete
                     </motion.button>
                  </motion.div>
               ) : (
                  <ChatSelect title={name} onSelect={() => console.log('pp')} />
               )}
            </motion.span>
         ) : null}
      </motion.span>
   );
}
