import React, { useState, useContext } from 'react';
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import userData from '../App';
import '../styles/nav.scss';
export default function Navbar() {
   const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
   ];
   const [margin, setMargin] = useState({ margin: '10px' });
   const [month, setMonth] = useState(null);
  const user = useContext(userData);
  useEffect(() => {
    window.addEventListener('scroll', (e) => {
       if (window.scrollY >= 40) {
          setMargin({ margin: '10px 60px', opacity:1 });
       } else {
           setMargin({ margin: '10px', opacity: 0 });
       }
    });
  },[])

   return (
      <nav
         class="navbar navbar-expand-lg navbar-light bg-transparent pl-2"
         style={margin}
      >
         <span class="navbar-brand" href="#">
            &nbsp;Yocal
         </span>
         <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNavDropdown"
            aria-controls="navbarNavDropdown"
            aria-expanded="false"
            aria-label="Toggle navigation"
         >
            <span class="navbar-toggler-icon"></span>
         </button>
         <div class="collapse navbar-collapse" id="navbarNavDropdown">
            <ul class="navbar-nav">
               <li class="nav-item active">
                  <Link class="nav-link" to="/home">
                     Home <span class="sr-only">(current)</span>
                  </Link>
               </li>
               <li class="nav-item">
                  <Link class="nav-link" to="/friends">
                     Friends
                  </Link>
               </li>
               <li class="nav-item dropdown">
                  <span
                     class="nav-link dropdown-toggle disabled"
                     href="#"
                     id="navbarDropdownMenuLink"
                     data-toggle="dropdown"
                     aria-haspopup="true"
                     aria-expanded="false"
                  >
                     Create
                  </span>
                  <div
                     class="dropdown-menu"
                     aria-labelledby="navbarDropdownMenuLink"
                  >
                     {months.map((month, index) => (
                        <span
                           onMouseEnter={() => setMonth(index)}
                           class="dropdown-item"
                        >
                           {months[index]}
                        </span>
                     ))}
                  </div>
                  <div
                     class="dropdown-menu"
                     aria-labelledby="navbarDropdownMenuLink"
                  ></div>
               </li>
               {/*<li class="nav-item">
            <Link class="nav-link" to="/chat">Chat</Link>
        </li>*/}
            </ul>
         </div>
      </nav>
   );
}
