// import input from '@material-ui/core/input';
// import './App.css';

import Navbar from './components/Nav';
import React, { useEffect, useState, createContext, useContext } from 'react';
import './styles/calendar.scss';
import './styles/App.css';
import Login from './components/Login';
import Home from './components/Home';
//import Home from './components/newhome';
import {
   BrowserRouter as Router,
   Route,
   Switch,
   Redirect,
   useLocation,
   useHistory,
} from 'react-router-dom';
import Createevent from './components/CreateEvent';
import './styles/Event.scss';
import axios from 'axios';
import Friends from './components/Friends';
import BetaFooter from './components/BetaFooter';
import Edit from './components/Edit';
import './styles/toruskit.css';
import Chat from './components/Chat/chatHome';
import Spinner from './microComponents/spinner';
import Archive from './components/Archive';
import { valify } from './maps';


import io from 'socket.io-client';
Map.prototype.valify = valify;
// import Conflicts from "./components/Conflicts"
//use via props vs via a freq called func becasue it will give better performance!
export const userData = createContext();
export const Socket = createContext();
let socket = io('http://localhost:4000');
const App = () => {
   //create a function here that will create the props data. if it cannot find the data for the prop, redirect and/or return null.
   const [state, setState] = useState({
      username: null,
      data: null,
      events: null,
      friendEvents: null,
      /*--------------------*/
      friendReqs: null,
      friends: null,
      /*--------------------*/
      month: null,
      year: null,
      /* ------------------ */
      chats: null,
      messages: null,
   });

   useEffect(() => {
      // connect(socket)
   }, []);
   const history = useHistory();
   const location = useLocation();

   useEffect(() => {
      console.log('sdfasdfas');
      socket.on('connection', () => {
         console.log('connected!@');
         socket.emit('join');
      });
      socket.on('begin-connection', (res) => {
         setState(res);
         history.push('/home');
         socket.on('time', (f) => {
            console.log(f, '!time@');
         });
         socket.on('time', (m) => {
            console.log(m, 'room1 ');
         });
      });
      if (
         localStorage.getItem('token') !== null &&
         window.location.pathname === '/'
      ) {
         socket.emit('login', localStorage.getItem('token')); //distinguish
      }
   }, [socket]);
   //dont allow users to enter without logging in
   useEffect(() => {
      if (location.pathname !== '/' && localStorage.getItem('token') === null) {
         history.push('/');
      }
   });
   useEffect(() => {
      console.log('state updated!');
      console.table(state);
   }, [state]);

   useEffect(() => {
      getData();
   }, []);
   //const [IO, setIO] = useState(false)
   let IO
   return (
      <div>
         <Socket.Provider value={socket}>
            <userData.Provider value={[state, setState]}>
               {state.username == null && window.location.pathname != '/' ? (
                  <Spinner />
               ) : (
                     <>
                        <button onClick={()=>IO.emit('ping', res=>console.log(res))}>peeng</button>
                     {state.username&&<Navbar />}
                     <button onClick={() => console.log(state.events)}>
                        sdfasdf
                     </button>
                     <Switch>
                        <Route path="/" exact component={Login} />
                        <Route path="/home" exact component={Home} />
                        <Route
                           exact
                           path="/home/:day/:month/:year"
                           component={Createevent}
                        />
                        <Route exact path="/friends" component={Friends} />
                        <Route exact path="/chat" component={Chat} />
                        <Route exact path="/edit/:id" component={Edit} />
                        <Route exact path="/archive" component={Archive} />
                        <Route render={() => <h1>404 error</h1>} />
                     </Switch>
                     <BetaFooter />
                  </>
               )}
            </userData.Provider>
         </Socket.Provider>
      </div>
   );
   async function getData() {
      try {
         //auth with token
         const { data } = await axios.get('http://localhost:8000/getuser', {
            headers: {
               Authorization: 'Bearer ' + localStorage.getItem('token'),
            },
         });
         //set userdata
         setState({
            ...data,
            events: new Map(data.events).valify(),
         });
         const socket = io('http://localhost:4000');
         socket.on('pong', () => {
            console.log('pong!')
         })
         setIO(socket)

         //console.log(data,"data")
         //go to home
         //
         if (location.pathname === '/') {
            history.push('/home');
         }
      } catch (e) {
         history.push('/');
         console.error(e);
         console.log('ERROR');
      } finally {
         console.log('getdata called in appjs');
      }
   }
};
export default App;
